import React from 'react';

class Quotes extends React.component {
    constructor(props) {
        super(props);
        this.state = {
            quote: "Excelsior!",
            author: "- Stan Lee"
        }
    }
    render() {
        // const possibleQuotes = [
        //     {
        //     quote: "",
        //     author: ""
        //     }
        // ]
        return(
            <div className="quote-bubble">
                <h2 className="quote" id="text">
                    {this.state.quote}
                </h2>
                <h3 className="author" id="author">
                    {this.state.author}
                </h3>

            </div>
        );
    }
}

export default Quotes;
