import React from 'react';



class Quotes extends React.component {
    constructor(props) {
        super(props);
        this.state = {
            quote: "",
            author: ""
        }
    }
    render() {
        const quotes = [
            {
            quote: "Excelsior!",
            author: "- Stan Lee"
            }
        ]
        return(
            <div className="quote-bubble">
                <h2 className="quote" id="text">
                    {quotes.quote}
                </h2>
                <h3 className="author" id="author">
                    {quotes.author}
                </h3>

            </div>
        );
    }
}

export default Quotes;
